import React, { useState, useEffect } from "react";
import axios from "axios";
// import { FaPlus, FaTrash } from "react-icons/fa";

const API_URL = "http://localhost:5000/api/todos"; // Change if needed

const App = () => {
  const [todos, setTodos] = useState([]);
  const [task, setTask] = useState("");

  // Fetch todos from backend
  const fetchTodos = async () => {
    try {
      const response = await axios.get(API_URL);
      setTodos(response.data);
    } catch (error) {
      console.error("Error fetching todos:", error);
    }
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  // Add a new todo
  const addTodo = async () => {
    if (!task.trim()) return;
    try {
      const response = await axios.post(API_URL, { task });
      setTodos([...todos, response.data]); // Update state
      setTask("");
    } catch (error) {
      console.error("Error adding todo:", error);
    }
  };

  // Delete a todo
  const deleteTodo = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      setTodos(todos.filter(todo => todo._id !== id));
    } catch (error) {
      console.error("Error deleting todo:", error);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>To-Do List</h1>

      <div style={styles.inputContainer}>
        <input
          type="text"
          placeholder="Add a task..."
          value={task}
          onChange={(e) => setTask(e.target.value)}
          style={styles.input}
        />
        <button onClick={addTodo} style={styles.addButton}>
          <FaPlus />
        </button>
      </div>

      <ul style={styles.list}>
        {todos.map((todo) => (
          <li key={todo._id} style={styles.listItem}>
            {todo.task}
            <button onClick={() => deleteTodo(todo._id)} style={styles.deleteButton}>
              <FaTrash />
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

// Inline styles
const styles = {
  container: { textAlign: "center", padding: "20px", fontFamily: "Arial" },
  heading: { fontSize: "28px", marginBottom: "10px" },
  inputContainer: { display: "flex", justifyContent: "center", marginBottom: "10px" },
  input: { padding: "10px", fontSize: "16px", width: "250px", borderRadius: "5px", border: "1px solid #ccc" },
  addButton: { marginLeft: "5px", padding: "10px", backgroundColor: "#28a745", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" },
  list: { listStyle: "none", padding: "0" },
  listItem: { display: "flex", justifyContent: "space-between", alignItems: "center", backgroundColor: "#f4f4f4", padding: "10px", borderRadius: "5px", margin: "5px 0" },
  deleteButton: { padding: "5px", backgroundColor: "#dc3545", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }
};

export default App;
